import * as React from 'react';
import { useState } from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import useMediaQuery from '@mui/material/useMediaQuery';
import PersonAddAltIcon from '@mui/icons-material/PersonAddAlt';
import LogoutIcon from '@mui/icons-material/Logout';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import IconButton from '@mui/material/IconButton/IconButton';
import { useHistory } from 'react-router-dom';
import ApiAccess from '../services/api-access';
import MyModal from './MyModal';
import Modal from 'react-modal';

Modal.setAppElement('#root');

const pages = ['Enregistrement étudiant'];
const settings = ['Profil', 'Deconnexion'];

function UserNavBar() {

    // Check if the screen size is medium or smaller
    const isMediumScreen = useMediaQuery('(max-width: 800px)');
    const [isLogoutDialogOpen, setLogoutDialog] = useState(false);

    const LogoutManagement = () => {
        ApiAccess.logout();
    }

    const history = useHistory();

    const goToPage = (path: any) => {
        history.push(path);
    };

    const openLogoutDialog = () => {
        setLogoutDialog(true);
    }

    const closeLogoutDialog = () => {
        setLogoutDialog(false);
    }

    return (
        <React.Fragment>
            <AppBar position="static">
                <Container maxWidth="xl">
                    <Toolbar sx={{ justifyContent: 'space-between' }} disableGutters>
                        <Box>
                            {isMediumScreen ? (
                                <React.Fragment>
                                    <IconButton
                                        size="large"
                                        aria-label="add student button"
                                        aria-haspopup="true"
                                        color="inherit"
                                        onClick={() => goToPage('/student/registration')}
                                    >
                                        <PersonAddAltIcon />
                                    </IconButton>
                                </React.Fragment>
                            ) : (
                                pages.map((page) => (
                                    <Button key={page} sx={{ color: 'inherit', mr: 2 }}>
                                        {page}
                                    </Button>
                                ))
                            )}
                        </Box>

                        <Typography
                            variant="h6"
                            noWrap
                            component="a"
                            href="#app-bar-with-responsive-menu"
                            sx={{
                                fontFamily: 'monospace',
                                fontWeight: 700,
                                color: 'inherit',
                                textDecoration: 'none',
                            }}
                        >
                            Students APP
                        </Typography>
                        <Box>
                            {isMediumScreen ? (
                                <React.Fragment>
                                    <IconButton
                                        size="large"
                                        aria-label="deconnection"
                                        aria-haspopup="true"
                                        color="inherit"
                                        onClick={openLogoutDialog}
                                    >
                                        <LogoutIcon />
                                    </IconButton>
                                    <IconButton
                                        size="large"
                                        aria-label="account update"
                                        aria-haspopup="true"
                                        color="inherit"
                                        onClick={() => goToPage('/profil')}
                                    >
                                        <AccountCircleIcon />
                                    </IconButton>
                                </React.Fragment>
                            ) : (
                                settings.map((setting) => (
                                    <Button key={setting} sx={{ color: 'inherit' }} >
                                        {setting}
                                    </Button>
                                ))
                            )}
                        </Box>
                    </Toolbar>
                </Container>
            </AppBar>

            <MyModal isOpen={isLogoutDialogOpen} onClose={closeLogoutDialog} onConfirm={LogoutManagement}>
                Vous allez vous déconnecté
                Êtes vous sûr de continuer ?
            </MyModal>
        </React.Fragment>

    );
}
export default UserNavBar;
