import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import IconButton from '@mui/material/IconButton';
import Stack from '@mui/material/Stack';
import useMediaQuery from '@mui/material/useMediaQuery';
import PersonAddAltIcon from '@mui/icons-material/PersonAddAlt';
import LogoutIcon from '@mui/icons-material/Logout';
import DeleteSweepIcon from '@mui/icons-material/DeleteSweep';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import ListIcon from '@mui/icons-material/List';
import SlideDialog from './MaterialModal';
import { Snackbar } from '@mui/material';
import ConnectedAccount from '../models/ConnectedAccount';
import Loader from './loader';
import library from '../helpers/library';
import Title from './Title';
import CloseIcon from '@mui/icons-material/Close';
import ApiAccess from '../services/api-access';
import { useHistory } from 'react-router-dom';
import routes from '../helpers/routes';
import cookieServiceInstance from '../services/cookies';
import Account from '../models/Account';

const NavBar: React.FC = () => {
    const isMediumScreen = useMediaQuery('(max-width: 800px)');
    const [isConnected, setConnected] = React.useState(false);
    const [isAdmin, setAdmin] = React.useState(false);
    const [loading, setLoading] = React.useState(false);
    const [isLogoutDialogOpen, setLogoutDialogOpen] = React.useState(false);
    const [isClearDialogOpen, setClearDialogOpen] = React.useState(false);
    const [isConnexionSnackOpen, setConnexionSnackOpen] = React.useState(false);

    React.useEffect(() => {
        if (ConnectedAccount.id) {
            ConnectedAccount.connect(new Account())
        }
        retrieveDataAfterLoad();
    }, []);

    function retrieveDataAfterLoad() {
        if(cookieServiceInstance.getCookie(library.account)) {
            ConnectedAccount.connect(JSON.parse(cookieServiceInstance.getCookie(library.account)!));
            ConnectedAccount.authToken = cookieServiceInstance.getCookie(library.token)!;
        }
        setAdmin(ConnectedAccount.roleB);
    }

    document.addEventListener('DOMContentLoaded', retrieveDataAfterLoad);

    if (ConnectedAccount.username) {
        setAdmin(ConnectedAccount.roleB);
        setConnected(true);
    }

    const LogoutManagement = async () => {
        setLoading(true);
        try {
            await ApiAccess.logout();
        } finally {
            goToPage(routes.connexion);
            setLogoutDialogOpen(false);
            setLoading(false);
            window.location.reload();
        }
    }

    const ClearBase = () => {
        ApiAccess.clearBase();
    }

    const history = useHistory();

    const goToPage = (path: any) => {
        history.push(path);
    };

    const openLogoutDialog = () => {
        setLogoutDialogOpen(true);
    }

    const closeLogoutDialog = () => {
        setLogoutDialogOpen(false);
    }

    const openClearDialog = () => {
        setClearDialogOpen(true);
    }

    const closeClearDialog = () => {
        setClearDialogOpen(false);
    }

    const openSnack = () => {
        if(!isConnexionSnackOpen) {
            setConnexionSnackOpen(true);
        }   
    }

    const closeSnack = () => {
        setConnexionSnackOpen(false);
    }

    const action = (
        <React.Fragment>
          <IconButton
            size="small"
            aria-label="close"
            color="inherit"
            onClick={closeSnack}
          >
            <CloseIcon fontSize="small" />
          </IconButton>
        </React.Fragment>
      );

    return (
        <React.Fragment>
            <AppBar position="static">
                <Container maxWidth="xl">
                    <Toolbar sx={{ justifyContent: 'space-between' }} disableGutters>
                        <Stack>
                            {isConnected ? (
                                <>
                                    {isAdmin ? (
                                        <>
                                            {isMediumScreen ? (
                                                <Stack direction="row" spacing={1}>
                                                    <IconButton
                                                        size="large"
                                                        aria-label="accounts list"
                                                        aria-haspopup="true"
                                                        color="inherit"
                                                        onClick={() => goToPage(routes.addstudent)}
                                                    >
                                                        <PersonAddAltIcon />
                                                    </IconButton>
                                                    <IconButton
                                                        size="large"
                                                        aria-label="account update"
                                                        aria-haspopup="true"
                                                        color="inherit"
                                                        onClick={openClearDialog}
                                                    >
                                                        <DeleteSweepIcon />
                                                    </IconButton>
                                                </Stack>
                                            ) : (
                                                <>
                                                    <Stack direction="row" spacing={1}>
                                                        <Button sx={{ color: 'inherit' }} onClick={() => goToPage(routes.addstudent)}>
                                                            {library.studentRegister}
                                                        </Button>
                                                        <Button sx={{ color: 'inherit' }} onClick={openClearDialog}>
                                                            {library.dbSweep}
                                                        </Button>
                                                    </Stack>
                                                    <Title />
                                                    <Stack direction="row" spacing={1}>
                                                        <Button sx={{ color: 'inherit' }} onClick={() => goToPage(routes.accounts)}>
                                                            {library.accountList}
                                                        </Button>
                                                        <Button sx={{ color: 'inherit' }} onClick={() => goToPage(routes.profil)}>
                                                            {library.profil}
                                                        </Button>
                                                        <Button sx={{ color: 'inherit' }} onClick={openLogoutDialog}>
                                                            {library.deconnection}
                                                        </Button>
                                                    </Stack>
                                                </>
                                            )}
                                        </>
                                    ) : (
                                        <>
                                            {isMediumScreen ? (
                                                <Stack direction="row" spacing={1}>
                                                    <IconButton
                                                        size="large"
                                                        aria-label="accounts list"
                                                        aria-haspopup="true"
                                                        color="inherit"
                                                        onClick={() => goToPage(routes.addstudent)}
                                                    >
                                                        <PersonAddAltIcon />
                                                    </IconButton>
                                                </Stack>
                                            ) : (
                                                <Stack direction="row" spacing={1}>
                                                    <Button sx={{ color: 'inherit' }} onClick={() => goToPage(routes.addstudent)}>
                                                        {library.studentRegister}
                                                    </Button>
                                                </Stack>
                                            )}
                                            <Title />
                                            <Stack direction="row" spacing={1}>
                                                <Button sx={{ color: 'inherit' }} onClick={() => goToPage(routes.profil)}>
                                                    {library.profil}
                                                </Button>
                                                <Button sx={{ color: 'inherit' }} onClick={openLogoutDialog}>
                                                    {library.deconnection}
                                                </Button>
                                            </Stack>
                                        </>
                                    )}
                                </>
                            ) : (
                                <>
                                    {isMediumScreen ? (
                                        <Stack direction="row" spacing={1}>
                                            <IconButton
                                                size="large"
                                                aria-label="accounts list"
                                                aria-haspopup="true"
                                                color="inherit"
                                                onClick={openSnack}
                                            >
                                                <PersonAddAltIcon />
                                            </IconButton>
                                            <IconButton
                                                size="large"
                                                aria-label="account update"
                                                aria-haspopup="true"
                                                color="inherit"
                                                onClick={openSnack}
                                            >
                                                <DeleteSweepIcon />
                                            </IconButton>
                                        </Stack>
                                    ) : (
                                        <Stack direction="row" spacing={1}>
                                            <Button sx={{ color: 'inherit' }} onClick={openSnack}>
                                                {library.studentRegister}
                                            </Button>
                                            <Button sx={{ color: 'inherit' }} onClick={openSnack}>
                                                {library.dbSweep}
                                            </Button>
                                        </Stack>
                                    )}
                                    <Title />
                                    {isMediumScreen ? (
                                        <Stack direction="row" spacing={1}>
                                            <IconButton
                                                size="large"
                                                aria-label="accounts list"
                                                aria-haspopup="true"
                                                color="inherit"
                                                onClick={() => goToPage(routes.accounts)}
                                            >
                                                <ListIcon />
                                            </IconButton>
                                            <IconButton
                                                size="large"
                                                aria-label="account update"
                                                aria-haspopup="true"
                                                color="inherit"
                                                onClick={() => goToPage(routes.profil)}
                                            >
                                                <AccountCircleIcon />
                                            </IconButton>
                                            <IconButton
                                                size="large"
                                                aria-label="deconnection"
                                                aria-haspopup="true"
                                                color="inherit"
                                                onClick={openLogoutDialog}
                                            >
                                                <LogoutIcon />
                                            </IconButton>
                                        </Stack>
                                    ) : (
                                        <Stack direction="row" spacing={1}>
                                            <Button sx={{ color: 'inherit' }} onClick={() => goToPage(routes.connexion)}>
                                                {library.connexion}
                                            </Button>
                                            <Button sx={{ color: 'inherit' }} onClick={() => goToPage(routes.register)}>
                                                {library.register}
                                            </Button>
                                        </Stack>
                                    )}
                                </>
                            )}
                        </Stack>
                    </Toolbar>
                </Container>
            </AppBar>

            <SlideDialog isOpen={isLogoutDialogOpen} onClose={closeLogoutDialog} onConfirm={LogoutManagement} title={'Deconnexion'}>
                {loading ? (
                    <Loader />
                ) : (
                    <>
                        <p>Vous allez vous déconnecter</p>
                        <p>Êtes vous sûr de continuer ?</p>
                    </>
                )}
            </SlideDialog>

            <SlideDialog isOpen={isClearDialogOpen} onClose={closeClearDialog} onConfirm={ClearBase} title={'Vider la base'}>
                {loading ? (
                    <Loader />
                ) : (
                    <>
                        <p>Vous allez vider la base de donnée</p>
                        <p>Cette action est irreversible</p>
                        <p>Êtes vous sûr de vouloir continuer ?</p>
                    </>
                )}

            </SlideDialog>
            <Snackbar
                anchorOrigin={{vertical: 'bottom', horizontal: 'center'}}
                open={isConnexionSnackOpen}
                autoHideDuration={3000}
                onClose={closeSnack}
                message="Vous devez vous connecter en premier"
                action={action}
            />
        </React.Fragment>
    );
}

export default NavBar;
