import Student from "../models/Student";

const jsonUrl: string = '/test_data.json';

export default class DataAccess {


    static getRandoDatas(): Promise<Student> {
        return fetch(jsonUrl)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                return response.json();
            })
            .then(data => {
                console.table(data);
                let fnList: string[] = data.firstnames;
                let lnList: string[] = data.lastnames;
                let snList: string[] = data.streetnames;

                let randoStud = new Student();

                randoStud.name = this.prototype.listRand(lnList) + " " + this.prototype.listRand(fnList);
                randoStud.address = this.prototype.listRand(snList);
                return randoStud;
            })
    }

    listRand(list: string[]): string {
        const randIndex = Math.floor(Math.random() * list.length);
        const randElement = list[randIndex];
        return randElement;
    }
}
