import * as React from 'react';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import Button from '@mui/material/Button';
import Tooltip from '@mui/material/Tooltip';
import MenuItem from '@mui/material/MenuItem';
import LoginIcon from '@mui/icons-material/Login';
import useMediaQuery from '@mui/material/useMediaQuery';
import PersonAddAltIcon from '@mui/icons-material/PersonAddAlt';
import DeleteSweepIcon from '@mui/icons-material/DeleteSweep';
import PersonAddIcon from '@mui/icons-material/PersonAdd';
import MenuIcon from '@mui/icons-material/Menu';
import IconButton from '@mui/material/IconButton/IconButton';

const pages = ['Student registration', 'Clear base'];
const settings = ['Login', 'Sign in'];

function ResponsiveAppBar() {
  const [anchorElUser, setAnchorElUser] = React.useState<null | HTMLElement>(null);

  const handleOpenUserMenu = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorElUser(event.currentTarget);
  };

  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };

  // Check if the screen size is medium or smaller
  const isMediumScreen = useMediaQuery('(max-width: 800px)');

  return (
    <AppBar position="static">
      <Container maxWidth="xl">
        <Toolbar sx={{ justifyContent: 'space-between' }} disableGutters>
          <Box>
            {isMediumScreen ? (
              <React.Fragment>
                <IconButton
                  size="large"
                  aria-label="add student button"
                  aria-haspopup="true"
                  onClick={handleOpenUserMenu}
                  color="inherit"
                >
                  <PersonAddAltIcon />
                </IconButton>
                <IconButton
                  size="large"
                  aria-label="clear base button"
                  aria-haspopup="true"
                  onClick={handleOpenUserMenu}
                  color="inherit"
                >
                  <DeleteSweepIcon />
                </IconButton>
              </React.Fragment>
            ) : (
              pages.map((page) => (
                <Button key={page} sx={{ color: 'inherit', mr: 2 }}>
                  {page}
                </Button>
              ))
            )}
          </Box>

          <Typography
            variant="h6"
            noWrap
            component="a"
            href="#app-bar-with-responsive-menu"
            sx={{
              fontFamily: 'monospace',
              fontWeight: 700,
              color: 'inherit',
              textDecoration: 'none',
            }}
          >
            Students APP
          </Typography>
          <Box>
            {isMediumScreen ? (
              <React.Fragment>
              <IconButton
                size="large"
                aria-label="add student button"
                aria-haspopup="true"
                onClick={handleOpenUserMenu}
                color="inherit"
              >
                <LoginIcon />
              </IconButton>
              <IconButton
                size="large"
                aria-label="clear base button"
                aria-haspopup="true"
                onClick={handleOpenUserMenu}
                color="inherit"
              >
                <PersonAddIcon />
              </IconButton>
            </React.Fragment>
            ) : (
              settings.map((setting) => (
                <Button key={setting} sx={{ color: 'inherit' }} onClick={handleCloseUserMenu}>
                  {setting}
                </Button>
              ))
            )}
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
}
export default ResponsiveAppBar;
