import React, { ChangeEvent, FunctionComponent, useState } from 'react';
import ApiAccess from '../services/api-access';
import Account from '../models/Account';
import Grid from '@mui/material/Grid/Grid';
import Typography from '@mui/material/Typography/Typography';
import Paper from '@mui/material/Paper/Paper';
import TextField from '@mui/material/TextField/TextField';
import Button from '@mui/material/Button/Button';
import ConnectedAccount from '../models/ConnectedAccount';

const ProfilEdition: FunctionComponent = () => {

    const [profilEditionForm, setProfilEditionForm] = useState({
        username: ConnectedAccount.username,
        password: '',
        email: ConnectedAccount.email
    });

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setProfilEditionForm({
            ...profilEditionForm,
            [name]: value,
        });
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        let account: Account = new Account(profilEditionForm.username, profilEditionForm.password, profilEditionForm.email, ConnectedAccount.role);
        account.id = ConnectedAccount.id;
        console.table(account);
        account = await ApiAccess.editAccount(account);
        ConnectedAccount.username = account.username;
        ConnectedAccount.email = account.email; 
    };

    return (
        <Grid container justifyContent="center" alignItems="top" style={{ minHeight: '100vh' }}>
            <Grid item xs={10} sm={6} md={4}>
                <Paper elevation={3} style={{ padding: '20px' }}>
                    <Typography variant="h5" component="h2" gutterBottom>
                        Mise a jour du profil
                    </Typography>
                    <form onSubmit={handleSubmit}>
                        <TextField
                            label="Username"
                            name="username"
                            value={profilEditionForm.username}
                            onChange={handleChange}
                            fullWidth
                            margin="normal"
                            variant="standard"
                        />
                        <TextField
                            label="Password"
                            name="password"
                            type="password"
                            value={profilEditionForm.password}
                            onChange={handleChange}
                            fullWidth
                            margin="normal"
                            variant="standard"
                        />
                        <TextField
                            label="Email"
                            name="email"
                            type="text"
                            value={profilEditionForm.email}
                            onChange={handleChange}
                            fullWidth
                            margin="normal"
                            variant="standard"
                        />
                        <Button type="submit" variant="contained" color="primary" fullWidth>
                            Editer
                        </Button>
                    </form>
                </Paper>
            </Grid>
        </Grid>
    );
}

export default ProfilEdition;
