import React from 'react';
import Modal from 'react-modal';

interface MyModalProps {
  isOpen: boolean;
  onClose: () => void;
  onConfirm: () => void;
  children: React.ReactNode;
}

const MyModal = ({ isOpen, onClose, onConfirm,  children }: MyModalProps) => {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onClose}
      style={{
        overlay: {
          backgroundColor: 'rgba(0, 0, 0, 0.5)'
        },
        content: {
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: '50%',
          maxWidth: '300px',
          maxHeight: '300px'
        }
      }}
    >
      {children}
      <div style= {{
        display: 'flex',
        alignItems: 'center'
      }}>
      <button style={{
        marginInlineEnd: '10%'
      }}
      onClick={onClose}>Non</button>
      <button onClick={onConfirm}>Oui</button>
      </div>
    </Modal>
  );
};

export default MyModal;
