import React, { ChangeEvent, FunctionComponent, useState } from 'react';
import { TextField, Button, Grid, Paper, Typography } from '@mui/material';
import ApiAccess from '../services/api-access';
import ConnectedAccount from '../models/ConnectedAccount';
import Credentials from '../models/Credentials';
import { useHistory } from 'react-router-dom';
import cookieServiceInstance from '../services/cookies';
import library from '../helpers/library';
import { ToastContainer, toast } from 'react-toastify';
import routes from '../helpers/routes';
import Loader from '../component/loader';

const ConnectionPage: FunctionComponent = () => {
  const history = useHistory();
  const [loading, setLoading] = useState(false);
  const [connectionForm, setConnectionForm] = useState({
    username: '',
    password: '',
  });
  const BadCredentialnotif = () => toast('L\'identifiant ou le mot de passe n\'a pas été reconnu');

  const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setConnectionForm({
      ...connectionForm,
      [name]: value,
    });
  };

  const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    setLoading(true);
    try {
      const accountResponse = await ApiAccess.login(new Credentials(connectionForm.username, connectionForm.password));
      console.log(accountResponse.token);
      if (accountResponse.token) {
        ConnectedAccount.connect(accountResponse.account);
        ConnectedAccount.authToken = accountResponse.token;
        cookieServiceInstance.setCookie(library.account, JSON.stringify(accountResponse.account), 2);
        cookieServiceInstance.setCookie(library.token, accountResponse.token, 2);
        history.push(routes.home);
      } else {
        BadCredentialnotif();
        console.log("Bad credentials");
      }
    } catch (error) {
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <React.Fragment>
      <Grid container justifyContent="center" alignItems="normal" marginTop={5} style={{ minHeight: '100vh' }}>
        <Grid item xs={10} sm={6} md={6}>
          <ToastContainer />
          <Paper elevation={4} style={{ padding: '20px' }}>
            <Typography variant="h5" component="h2" gutterBottom>
              Connexion
            </Typography>
            {loading ? (
              <Loader />
            ) : (
              <form onSubmit={handleSubmit}>
                <TextField
                  label="Username"
                  name="username"
                  value={connectionForm.username}
                  onChange={handleChange}
                  fullWidth
                  margin="normal"
                  variant="standard"
                  required
                />
                <TextField
                  label="Password"
                  name="password"
                  type="password"
                  value={connectionForm.password}
                  onChange={handleChange}
                  fullWidth
                  margin="normal"
                  variant="standard"
                  required
                />
                <Button type="submit" variant="contained" color="primary" fullWidth>
                  Se connecter
                </Button>
              </form>
            )}
          </Paper>
        </Grid>
      </Grid>
    </React.Fragment>
  );
};

export default ConnectionPage;
