import Account from "./Account";

export default class ConnectedAccount {
    public static id: string;
    public static username: string;
    public static password: string;
    public static email: string;
    public static role: string;
    public static roleB: boolean;


    public static fillFields(account: Account) {
        ConnectedAccount.id = account.id;
        ConnectedAccount.username = account.username;
        ConnectedAccount.password = '';
        ConnectedAccount.email = account.email;
        ConnectedAccount.role = account.role;
        ConnectedAccount.roleB = account.role.includes("ADMIN");
    }

    public static clearFields(): void {
        ConnectedAccount.id = '';
        ConnectedAccount.username = '';
        ConnectedAccount.password = '';
        ConnectedAccount.email = '';
        ConnectedAccount.role = '';
    }

}
