import React from 'react';
import Modal from 'react-modal';
import '../styles/modalstyle.css';

interface MyModalProps {
  isOpen: boolean;
  onClose: () => void;
  onConfirm: () => void;
  children: React.ReactNode;
  title: React.ReactNode;
}

const MyModal = ({ isOpen, onClose, onConfirm, children, title }: MyModalProps) => {
  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onClose}
      style={{
        overlay: {
          backgroundColor: 'rgba(0, 0, 0, 0.5)'
        },
        content: {
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          width: '50%',
          maxWidth: '400px',
          maxHeight: '400px'
        }
      }}
    >
      <div className='modalContentContainer'>
        {title}
        {children}
        <div className='buttonContainer'>
          <button className='buttons' onClick={onClose}>Non</button>
          <button className='buttons' onClick={onConfirm}>Oui</button>
        </div>
      </div>
    </Modal>
  );
};

export default MyModal;
