import React, { ChangeEvent, FunctionComponent, useState } from 'react';
import ApiAccess from '../services/api-access';
import Student from '../models/Student';
import Grid from '@mui/material/Grid/Grid';
import Paper from '@mui/material/Paper/Paper';
import Typography from '@mui/material/Typography/Typography';
import TextField from '@mui/material/TextField/TextField';
import Button from '@mui/material/Button/Button';
import Loader from '../component/loader';
import { useHistory } from 'react-router-dom';
import routes from '../helpers/routes';
const StudentRegistration: FunctionComponent = () => {

    const [loading, setLoading] = useState(true);
    const [studentRegistrationForm, setStudentRegistrationForm] = useState({
        name: '',
        phoneNumber: '',
        email: '',
        address: ''
    });

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setStudentRegistrationForm({
            ...studentRegistrationForm,
            [name]: value,
        });
    };

    const history = useHistory();

    const goToPage = (path: any) => {
        history.push(path);
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setLoading(true);
        try {
            await ApiAccess.saveStudent(new Student(studentRegistrationForm.name, studentRegistrationForm.phoneNumber, studentRegistrationForm.email, studentRegistrationForm.address))
        } finally {
            setLoading(false);
            goToPage(routes.home);
        }
        
    };

    return (
        <Grid container justifyContent="center" alignItems="center" style={{ minHeight: '100vh' }}>
            <Grid item xs={10} sm={6} md={4}>
                <Paper elevation={3} style={{ padding: '20px' }}>
                    <Typography variant="h5" component="h2" gutterBottom>
                        Connexion
                    </Typography>
                    <form onSubmit={handleSubmit}>
                        <TextField
                            label="Nom et prenom"
                            name="name"
                            value={studentRegistrationForm.name}
                            onChange={handleChange}
                            fullWidth
                            margin="normal"
                            variant="standard"
                            required
                        />
                        <TextField
                            label="Numero de téléphone"
                            name="phoneNumber"
                            type="text"
                            value={studentRegistrationForm.phoneNumber}
                            onChange={handleChange}
                            fullWidth
                            margin="normal"
                            variant="standard"
                            required
                        />
                        <TextField
                            label="Email"
                            name="email"
                            type="text"
                            value={studentRegistrationForm.email}
                            onChange={handleChange}
                            fullWidth
                            margin="normal"
                            variant="standard"
                            required
                        />
                        <TextField
                            label="Adresse"
                            name="address"
                            type="text"
                            value={studentRegistrationForm.address}
                            onChange={handleChange}
                            fullWidth
                            margin="normal"
                            variant="standard"
                            required
                        />
                        {loading ? (
                            <Loader />
                        ) : (
                            <>
                                <Button type="submit" variant="contained" color="primary" fullWidth>
                                    Enregistrer
                                </Button>
                            </>
                        )}

                    </form>
                </Paper>
            </Grid>
        </Grid>
    );
}

export default StudentRegistration;
