import { Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@mui/material';
import React, { FunctionComponent, useEffect, useState } from 'react';
import Student from '../models/Student';
import ApiAccess from '../services/api-access';
import Loader from '../component/loader';

const StudentsList: FunctionComponent = () => {

    const [studentList, setList] = useState<Student[]>([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const students = await ApiAccess.getStudents();
                setList(students);
            } catch (error) {
                console.error('Error fetching students:', error);
                setError(true);
            } finally {
                setLoading(false);
            }
        }

        fetchData();
    }, []);

    return (
        <div>
            {loading ? (
                <div className="center"><Loader /></div>
            ) : error ? (
                <h4 className="center">L'api ne semble pas joignable</h4>
            ) : studentList.length > 0 ? (
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Name</TableCell>
                                <TableCell>Phone Number</TableCell>
                                <TableCell>Email</TableCell>
                                <TableCell>Address</TableCell>
                                <TableCell></TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {studentList.map(student => (
                                <TableRow key={student.id}>
                                    <TableCell>{student.name}</TableCell>
                                    <TableCell>{student.phoneNumber}</TableCell>
                                    <TableCell>{student.email}</TableCell>
                                    <TableCell>{student.address}</TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            ) : (
                <h4 className="center">No students available</h4>
            )}
        </div>
    );
}

export default StudentsList;
