import React, { ChangeEvent, FunctionComponent, useState } from 'react';
import ApiAccess from '../services/api-access';
import Account from '../models/Account';
import Grid from '@mui/material/Grid/Grid';
import Typography from '@mui/material/Typography/Typography';
import Paper from '@mui/material/Paper/Paper';
import TextField from '@mui/material/TextField/TextField';
import Button from '@mui/material/Button/Button';
import Credentials from '../models/Credentials';
import ConnectedAccount from '../models/ConnectedAccount';
import cookieServiceInstance from '../services/cookies';
import library from '../helpers/library';
import AccountResponse from '../models/AccountResponse';
import { useHistory } from 'react-router-dom';
import routes from '../helpers/routes';
import Loader from '../component/loader';

const RegisterPage: FunctionComponent = () => {
    const history = useHistory();
    const [loading, setLoading] = useState(false);
    const [registerForm, setRegisterForm] = useState({
        username: '',
        password: '',
        email: ''
    });

    const handleChange = (e: ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setRegisterForm({
            ...registerForm,
            [name]: value,
        });
    };

    const handleSubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setLoading(true);
        try {
            await ApiAccess.saveAccount(new Account(registerForm.username, registerForm.password, registerForm.email))
            const newCredential: Credentials = new Credentials(registerForm.username, registerForm.password);
            const accountResponse: AccountResponse = await ApiAccess.login(newCredential);
            ConnectedAccount.connect(accountResponse.account);
            ConnectedAccount.authToken = accountResponse.token;
            cookieServiceInstance.setCookie(library.account, JSON.stringify(accountResponse.account), 2);
            cookieServiceInstance.setCookie(library.token, accountResponse.token, 2);
        } finally {
            history.push(routes.home);
            window.location.reload();
        }        
    };

    return (
        <Grid container justifyContent="center" alignItems="normal" marginTop={5} style={{ minHeight: '100vh' }}>
            <Grid item xs={10} sm={6} md={6}>
                <Paper elevation={4} style={{ padding: '20px' }}>
                    <Typography variant="h5" component="h2" gutterBottom>
                        Enregistrement
                    </Typography>
                    {loading ? (
                        <Loader />
                    ) : (
                        <form onSubmit={handleSubmit}>
                        <TextField
                            label="Username"
                            name="username"
                            value={registerForm.username}
                            onChange={handleChange}
                            fullWidth
                            margin="normal"
                            variant="standard"
                            required
                        />
                        <TextField
                            label="Password"
                            name="password"
                            type="password"
                            value={registerForm.password}
                            onChange={handleChange}
                            fullWidth
                            margin="normal"
                            variant="standard"
                            required
                        />
                        <TextField
                            label="Email"
                            name="email"
                            type="text"
                            value={registerForm.email}
                            onChange={handleChange}
                            fullWidth
                            margin="normal"
                            variant="standard"
                            required
                        />
                        <Button type="submit" variant="contained" color="primary" fullWidth>
                            S'enregistrer
                        </Button>
                    </form>
                    )}
                    
                </Paper>
            </Grid>
        </Grid>
    );
}

export default RegisterPage;
